section {
  /*Height Is 50vh & width is 70vw*/
  grid-row: 1/4;
  grid-column: 3;
  height: 100%;
  width: 100%;
  display: grid;
  grid-template-rows: 30vh 10vh 10vh;
  grid-template-columns: 10vw 50vw 10vw;
}
h2.question-prompt {
  color: rgba(98, 77, 77, 0.5);
  font-size: 4rem;
  font-weight: 200;
  grid-row: 1;
  grid-column: 2;
  text-align: center;
}

.question-prompt-container form {
  grid-row: 2;
  grid-column: 2;
}

.question-prompt-container form input {
  box-shadow: inset 2px 2px 2px 2px rgba(0, 0, 0, 0.5);
  text-align: center;
  border: 1px solid #85f8d6;
  color: #66bea4;
  padding: 1%;
  letter-spacing: 0.1rem;
  font-size: 3rem;
  font-weight: 200;
  width: 100%;
  height: 100%;
}

form input textarea {
  color: red;
}

button.question-input-commit {
  font-size: 3.5rem;
  font-weight: 300;
  background: #90b9ad;
  grid-row: 2;
  grid-column: 3;
  width: 80%;
  height: 100%;
  margin: auto;
  padding: 10%;
  text-align: center;
  box-shadow: 2px 2px 5px 0px rgba(0, 0, 0, 0.5);
}

.question-input-commit:hover {
  background: #4fbb9b;
  filter: drop-shadow(2px 2px 4px rgba(0, 0, 0, 0.5));
}
