* {
  box-sizing: border-box;
  color: #333;
}

html {
  font-size: 62.5%;
}
body {
  font-size: 1.6rem;
  background-color: #e9e9e9;
  margin: 0;
  font-family: "Montserrat", Tahoma, Geneva, Verdana, sans-serif;
  position: relative;
}
/**********************************/
/********REACT-PORTAL-STYLES-START*******/
/**********************************/

.ReactModalPortal > div {
  opacity: 0;
}

.ReactModalPortal .ReactModal__Overlay {
  transition: opacity 200ms ease-in-out;
}

.ReactModalPortal .ReactModal__Overlay--after-open {
  opacity: 1;
  position: relative;
  z-index: 10;
}

.ReactModalPortal .ReactModal__Overlay--before-close {
  opacity: 0;
}

.modal__button--close {
  margin-bottom: 0;
}

.modal {
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: #000;
  overflow: hidden;
}

.ls-modal li {
  list-style: none;
}
